import React from "react";
import PT from "prop-types";

export default function CheckboxGroup(props) {
  function setValue(e, value) {
    let values = [];
    if (e.target.checked) {
      values = [...props.value, value];
    } else {
      values = props.value.filter(v => v !== value);
    }
    props.onChange && props.onChange(values);
  }

  const boxs = props.options.map(o => (
    <label key={o.value}>
      <input
        type="checkbox"
        checked={props.value.includes(o.value)}
        onChange={e => setValue(e, o.value)}
      />
      {o.label}
    </label>
  ));

  return <div>{boxs}</div>;
}

CheckboxGroup.propTypes = {
  value: PT.arrayOf(PT.string).isRequired,
  options: PT.arrayOf(
    PT.shape({
      label: PT.string,
      value: PT.string
    })
  ).isRequired,
  onChange:PT.func
};
