import React, { Component } from "react";
import "./index.css";

export default class Cover extends Component {
  render() {
    let cover = null;
    if (this.props.show) {
      cover = (
        <div className="cover" onClick={this.props.close()}>
          <div className="cover__in">{this.props.children}</div>
        </div>
      );
    }

    return <>{cover}</>;
  }
}
