import React, { Component } from "react";
import PT from "prop-types";

export default function group(Comp) {
  return class Group extends Component {
    static propTypes = {
      value: PT.arrayOf(PT.string).isRequired,
      options: PT.arrayOf(
        PT.shape({
          label: PT.string,
          value: PT.string
        })
      ).isRequired,
      onChange: PT.func
    };

    setValue(e, value) {
      let values = [];
      if (e.target.checked) {
        values = [...this.props.value, value];
      } else {
        values = this.props.value.filter(v => v !== value);
      }
      this.props.onChange && this.props.onChange(values);
    }

    render() {
      const boxs = this.props.options.map(o => (
        <Comp
          key={o.value}
          checked={this.props.value.includes(o.value)}
          label={o.label}
          onChange={e => this.setValue(e, o.value)}
        />
      ));

      return <div>{boxs}</div>;
    }
  };
}
