import React, { Component } from "react";
import "./index.css";

export default class Cover extends Component {
  state = {
    cover: null
  };

  render() {
    if (this.props.show) {
      this.setState({
        cover: (
          <div
            className="cover"
            onClick={this.props.close()}>
            <div className="cover__in">{this.props.children}</div>
          </div>
        )
      });
    }

    return <>{this.state.cover}</>;
  }
}
