// 16.x版本以前使用的api
import React, { Component } from "react";
import PT from "prop-types";

const contextType = {
  a: PT.number,
  b: PT.string.isRequired,
  add: PT.func.isRequired
};

function A(props, context) {
  return (
    <>
      我是A组件 a:{context.a}
      <B />
    </>
  );
}

A.contextTypes = contextType;

function B(props, context) {
  return (
    <>
      <p>a：{context.a}</p>
      <p>b：{context.b}</p>

      <button
        onClick={() => {
          context.add({
            a: context.a + 1,
            b: context.b + 2
          });
        }}>
        a加1 b加2
      </button>
    </>
  );
}

B.contextTypes = contextType;

export default class Context extends Component {
  state = {
    a: 123,
    b: "345"
  };

  static childContextTypes = contextType;

  getChildContext() {
    return {
      a: this.state.a,
      b: this.state.b,
      add: state => {
        this.setState(state);
      }
    };
  }

  render() {
    return (
      <div>
        <A />
      </div>
    );
  }
}
