    <div>
      a:{context.a},b:{context.b}
    </div>
  );
}

B.contextType = ctx;

export default class Context extends Component {
    state = {
        a:123,
        b:"345"
    }

  render() {
    return (
      <ctx.Provider value = {this.state}>
        <A />
      </ctx.Provider>
    );
  }
}
