import React, { Component } from "react";

export default class Form extends Component {
  state = {
    name: "marx"
  };

  updateState = (key, value) => {
    this.setState({
      [key]: value
    });
  };

  render() {
    return (
      <>
        <div>
          <input
            value={this.state.name}
            onChange={e => this.updateState("name", e.target.value)}
          />
        </div>

        <div>
          {Object.keys(this.state).map(s => (
            <p key={s}>
              {s}ï¼š{this.state[s]}
            </p>
          ))}
        </div>
      </>
    );
  }
}
