import React, { Component } from "react";

export default class LifeCycleSun extends Component {
  state = {
    n: 0
  };

  constructor(props) {
    super(props);

    console.log("constructor", "初始化阶段");
  }

  static getDerivedStateFromProps(props, state) {
    console.log("getDerivedStateFromProps","属性状态更新","props",props,"state",state)

  }





  render() {
    console.log("runder", "渲染");
    return (
      <div>
        <p>我是LifeCycle</p>
        <p>父组件的n:{this.props.n}</p>
        <p>我的的n:{this.state.n}</p>
        <button
          onClick={() => {
            this.setState({
              n: this.state.n - 1
            });
          }}>
          n-1
        </button>
      </div>
    );
  }
}
